INSERT INTO `unifence_service` (`object_code`, `type_code`, `object_date`, `object_name`, `object_name_en`, `object_text`, `object_text_en`, `object_embed`, `object_embed_en`, `page_code`, `spage_code`, `object_active`) VALUES
(1, 1, 1, 'Ремонт и отделка помещений', 'Repair and finishing facilities', '<p>\r\n<strong>Косметический ремонт<br />\r\n</strong>Косметический ремонт стоит от 1 500 рублей за м2.\r\n</p>\r\n<p>\r\n<strong>Экономичный ремонт<br />\r\n</strong>Экономичный ремонт стоит от 3 000 рублей за м2. Предусмотрена частичная замена электроустановочных изделий и сантехники.\r\n</p>\r\n<p>\r\n<strong>Стандартный ремонт<br />\r\n</strong>Стандартный ремонт стоит от 4 500 рублей за м2. Предусмотрена замена электроустановочных изделий и сантехники, локальное выравнивание стен.\r\n</p>\r\n<p>\r\n<strong>Капитальный ремонт<br />\r\n</strong>Капитальный ремонт стоит от 6 500 рублей за м2. Полная замена инженерных сетей (электрика и сантехника), с выравниванием стен, без серьёзной перепланировки и как правило без дизайн-проекта.\r\n</p>\r\n<p>\r\n<strong>\r\nРемонт бизнес-класса</strong><br />\r\nРемонт бизнес-класса стоит от 8 600 рублей за м2. Такой вид ремонта выполняется по индивидуальному дизайн-проекту с выравниванием всех поверхностей в ноль.\r\n</p>\r\n<p>\r\n<strong>\r\nЭксклюзивный ремонт<br />\r\n</strong>Эксклюзивный ремонт стоит от 13 500 рублей за м2. Такой вид ремонта выполняется по предварительно разработанному дизайн-проекту с реализацией любых сложных идей и применением эксклюзивных материалов.\r\n</p>\r\n<p>\r\n<br />\r\n<em>\r\nСтоимость работ ориентировочная из расчёта за м2 &laquo;по полу&raquo; &plusmn;10%. Все работы выполняются по детально разработанным сметам с чёткой формой отчётности. </em>\r\n</p>\r\n<p>\r\n<em>Для более точного расчета стоимости работ&nbsp;<a href="contacts.php">свяжитесь с менеджером</a>.<br />\r\n</em>\r\n</p>\r\n<p>\r\nПомимо отделки помещений мы готовы предложить Вам создание эксклюзивного, максимально функционального и эргономичного дизайна интерьера в выбранном Вами стиле.\r\n</p>\r\n', '', 'Ремонт может быть косметическим или капитальным, простым или сложным. Каким бы он ни был, важно, чтобы ремонт был сделан качественно, профессионально и в срок. Именно такие услуги предлагаем мы. Продуманно, аккуратно, надежно. От поклейки обоев, до технологически сложного ремонта по индивидуальному дизайн-проекту. От 1.5 тыс. руб. за м2.&nbsp;\r\n', '', 7, 6, 1),
(2, 1, 2, 'Малоэтажное строительство', 'Low-rise construction', '<p>\r\nСтроительство малоэтажных домов, коттеджей, таунхаусов является одним из основных направлений деятельности нашей компании. &laquo;Дока Ремонт&raquo; (ООО &laquo;ЭлитСтройСервис-М&raquo;) строит дома, коттеджи и таунхаусы, бани и другие типы зданий из монолита, кирпича и ячеистого бетона под ключ с жестким контролем качества на всех этапах строительства. Мы готовы выступить генподрядчиком в реализации проектов разной сложности, как собственной разработки, так и по заказу клиента. На все работы представляется гарантия.\r\n</p>\r\n<p>\r\nМы обеспечиваем индивидуальный подход и неукоснительное соблюдение строительных норм и технологических процессов.\r\n</p>\r\n<p>\r\nКвалифицированные строители с многолетним опытом работы обеспечат высокое качество работ и надежность возведённых конструкций. Строительство ведется круглый год в любых погодных условиях.\r\n</p>\r\n<p>\r\nЕсли у вас имеется собственный персонал, мы предоставляем услугу шефмонтажа. В этом случае наши специалисты консультируют рабочих Заказчика, проведут обучение и проконтролируют выполнение работ на объекте.  \r\n</p>\r\n<p>\r\n<strong>При строительстве осуществляются следующие виды работ:</strong>\r\n</p>\r\n<ul>\r\n	<li>подготовка строительной площадки;</li>\r\n	<li>разработка грунта и земляные работы;&nbsp;</li>\r\n	<li>устройство дренажа;&nbsp;</li>\r\n	<li>устройство фундамента;&nbsp;</li>\r\n	<li>возведение стен;</li>\r\n	<li>монтаж легких ограждающих конструкций;</li>\r\n	<li>устройство перекрытий;&nbsp;</li>\r\n	<li>устройство бетонных и железобетонных конструкций;</li>\r\n	<li>устройство кровли;&nbsp;</li>\r\n	<li>устройство полов;</li>\r\n	<li>монтаж окон и дверей;&nbsp;</li>\r\n	<li>обвязка котлов, монтаж системы отопления;&nbsp;</li>\r\n	<li>устройство систем водоснабжения и канализации;&nbsp;</li>\r\n	<li>монтаж систем вентиляции и кондиционирования;</li>\r\n	<li>монтаж электрических и слаботочных систем;&nbsp;</li>\r\n	<li>внутренние и наружные отделочные работы;</li>\r\n	<li>благоустройство территории, ландшафтные работы.</li>\r\n</ul>\r\n<p>\r\n&nbsp;\r\n</p>\r\n', '', '&laquo;Дока Ремонт&raquo;&nbsp;строит дома, коттеджи, таунхаусы и другие типы зданий из монолита, кирпича и ячеистого бетона под ключ с жестким контролем качества на всех этапах строительства. Мы готовы выступить генподрядчиком в реализации проектов разной сложности, как собственной разработки, так и по заказу клиента. На все работы представляется гарантия.\r\n', '', 8, 7, 1),
(3, 1, 3, 'Монтаж инженерных сетей', 'Installation of utilities', '<p>\r\nЕще одним из направлений деятельности компании &laquo;Дока Ремонт&raquo; (ООО &laquo;ЭлитСтройСервис-М&raquo;) является строительство и монтаж инженерных сетей. Мы производим полный цикл работ в данной области: проектирование, монтажные работы, пуско-наладочные работы, сервисно-техническая поддержка оборудования.\r\n</p>\r\n<p>\r\nИнженерные решения, предлагаемые нашей компанией энергоэффективны, сбалансированы и экономически выгодны.\r\n</p>\r\n<p>\r\nК инженерным сетям можно отнести:\r\n</p>\r\n<ul>\r\n	<li>вентиляция и кондиционирование воздуха;</li>\r\n	<li>пылеудаление;</li>\r\n	<li>отопление;</li>\r\n	<li>холодоснабжение и теплоснабжение;</li>\r\n	<li>внутреннее газоснабжение;</li>\r\n	<li>электрооборудование и электроосвещение;</li>\r\n	<li>пожаротушение и пожарная сигнализация;</li>\r\n	<li>охранная сигнализация, видеонаблюдение и ограничение доступа;</li>\r\n	<li>компьютерные сети;</li>\r\n	<li>связь и коммуникации.</li>\r\n</ul>\r\n<p>\r\n&nbsp;\r\n</p>\r\n<p>\r\nНаша компания предлагает как услуги комплексного строительства, так и обустройство отдельных элементов. Наши специалисты реализуют проекты различной сложности вне зависимости от назначения объекта (будь то жилые, административные, офисные помещения или торговые  промышленные, складские комплексы).\r\n</p>\r\n<p>\r\nВ работе могут быть использованы как импортные, так и отечественные материалы и оборудование в зависимости от задач Заказчика.  Высокая квалификация наших специалистов позволяет быстро, качественно и в срок решить все задачи по проектированию, монтажу и эксплуатации систем инженерного оборудования любой сложности.\r\n</p>\r\n', '', 'Одним из направлений деятельности компании &laquo;Дока Ремонт&raquo; является строительство и монтаж инженерных сетей. Мы производим полный цикл работ в данной области: проектирование, монтажные работы, пуско-наладочные работы, сервисно-техническая поддержку оборудования.&nbsp;\r\nНаши специалисты реализуют проекты различной сложности вне зависимости от назначения объекта.\r\n', '', 9, 8, 1),
